mapper.select:
select 

from t_bbs b
where b.id = #{id,jdbcType=INTEGER}

select , (select count(cc.id) from t_customer_collection cc where cc.target_id = b.id and cc.collection_type = 3) as collectionCount from t_bbs b where 1=1
order by b.create_time desc
limit 0, #{pageEnd}

select a.id,a.text,b.name,b.photo_url from t_bbs a left join t_customer b on b.id = a.customer_id 
where a.bbs_type = 2 and a.target_type = 1 and a.target_id = #{targetId,jdbcType=INTEGER}

     limit #{startRow}, #{pageSize}

select ,
(select count(cc.id) from t_customer_collection cc where cc.target_id = b.id and cc.collection_type = 3) as collectionCount
from t_bbs b
where b.parent_id = #{parentId,jdbcType=INTEGER}


    limit 0, #{pageEnd}

select
from t_bbs b where b.customer_id = #{customerId,jdbcType=INTEGER} and b.bbs_type = 1 and b.post_type = 0 and b.delete_user_id is null order by b.create_time desc
limit #{pageStart}, #{pageSize}

select
from t_bbs b where b.customer_id = #{customerId,jdbcType=INTEGER} and b.bbs_type = 3 and b.post_type = 2 and b.target_type = 0 order by b.create_time desc
limit #{pageStart}, #{pageSize}

select
from t_bbs b where b.bbs_type = 3 and b.post_type = 2 and b.target_type = 0 and b.customer_id != #{customerId,jdbcType=INTEGER} and b.target_id in (select id from t_bbs where customer_id = #{customerId,jdbcType=INTEGER} and bbs_type = 1 and (post_type = 1 or post_type = 0) and target_type = 0 and delete_user_id is null) order by b.create_time desc
limit #{pageStart}, #{pageSize}

select
from t_bbs b where b.customer_id = #{customerId,jdbcType=INTEGER} and b.bbs_type = 1 and b.post_type = 1 and b.target_type = 0 order by b.create_time desc
limit #{pageStart}, #{pageSize}

select
from t_bbs b where b.bbs_type = 1 and b.post_type = 1 and b.target_type = 0 and b.customer_id != #{customerId,jdbcType=INTEGER} and b.parent_id in(select id from t_bbs where customer_id = #{customerId,jdbcType=INTEGER} and bbs_type = 1 and (post_type = 1 or post_type = 0) and target_type = 0 and delete_user_id is null) order by b.create_time desc
limit #{pageStart}, #{pageSize}
