mapper.delete:
delete from t_item
where id in 

	#{item.id}

mapper.select:
select 
a.id, a.hotel_id, a.name, a.text, a.tel, a.position, a.score, a.is_used
from t_item a INNER JOIN t_item_tag_association ita on ita.item_id = a.id WHERE ita.item_tag_id = #{tagId,jdbcType=INTEGER}

    and a.hotel_id = #{hotelId,jdbcType=INTEGER}

order by score desc

    limit #{pageStart}, #{pageSize}
